def part1
  input = DATA.read.chomp
  chars = input.chars

  chars.each_with_index do |char, index|
    set = Set.new([char, chars[index + 1], chars[index + 2], chars[index + 3]])
    next if set.size < 4
    puts index + 4
    exit
  end
end

def part2
  input = DATA.read.chomp
  chars = input.chars

  chars.each_with_index do |char, index|
    set = Set.new((index...index + 14).map { |i| chars[i] })
    next if set.size < 14
    puts index + 14
    exit
  end
end

part2

